<style lang="scss">
    .illus {
        .stroke {
            fill: none;
            stroke: var(--clr-stroke);
            stroke-linecap: round;
            stroke-linejoin: round;
            stroke-width: 1.25px;
        }

        // list of all illustrattion color groups
        $illusClrs: white, offWhite, vanilla, silverWarm, skyBlue, orange, brown;
        // list of all color ranges
        $illusClrRanges: 0, 1, 2, 3, 4, 5;

        // automatically classes for all illusClrs
        @each $clr in $illusClrs {
            @each $range in $illusClrRanges {
                .#{$clr}-#{$range} {
                    fill: var(--clr-#{$clr}-illus-#{$range});
                }
            }
        }
    }
</style>