<style lang="scss">
    .siteOptCard {
        display: flex;
        flex-flow: column nowrap;
        gap: var(--pad-xxs);

        /* relative position for absolute positioning of .illu */
        position: relative;
        height: 32rem;
        padding: 1.8rem;
        /* default background color, overwritten later */
        background-color: var(--clr-150);

        /* round corners and prevent overflow */
        border-radius: 1rem;
        overflow: hidden;

        &:focus-visible {
            outline: var(--focus-outline) var(--clr-900);
            outline-offset: 0;
        }

        h2 {
            color: var(--clr-1000);
            opacity: 0.7;
        }

        .illu {
            position: absolute;
            right: 0;
            bottom: 0;
            left: 0;
        }
    }

    .stroke {
        fill: none;
        stroke-linecap: round;
        stroke-linejoin: round;
    }

    #findYourCity {
        background-color: #7BE6F5;

        .illu {
            margin-left: auto;
            width: 70%;
        }
    }

    .findYourCity {
        &-stroke {
            stroke: #030b43;
        }

        &-black {
            fill: #030b43;
        }

        &-blue {
            &-1 {
                fill: #69acea;
            }
        }

        &-bage {
            &-1 {
                fill: #dbc6ab;
            }

            &-2 {
                fill: #ba6461;
            }
        }

        &-purple {
            &-1 {
                fill: #465499;
            }

            &-2 {
                fill: #192b7a;
            }
        }

        &-aquamarine {
            &-1 {
                fill: #a3ffd6;
            }
        }
    }

    
    /* === BREAKPOINTS ======================== */
    @media only screen and (max-width: $breakpoint-smdesktop) {
        .siteOptCard {
            height: 28rem;;
        }
    }
</style>